import React, { useEffect, useState } from "react";
import { processImage } from "./pixellize";
import PaletteCreator from "./Components/PaletteCreator";
import Dropdown from "./Components/Dropdown";

function App() {
  const [image, setImage] = React.useState<File | null>(null);
  const [dragBoxText, setDragBoxText] = React.useState(
    "Drop an image to start."
  );
  const [resolution, setResolution] = useState<number>(() => {
    const defaultResolution = 64;
    const savedResoltion = localStorage.getItem("resolution");
    if (!savedResoltion) return defaultResolution;
    try {
      return Number.parseInt(savedResoltion);
    } catch (error) {
      console.log("Failed to get saved resolution:", error);
    }
    return defaultResolution;
  });
  const [paletteColors, setPaletteColors] = useState<string[]>(() => {
    const savedPalette = localStorage.getItem("palette");
    const defaultPalette = [
      "#fff4e0",
      "#8fcccb",
      "#449489",
      "#285763",
      "#2f2b5c",
      "#4b3b9c",
      "#457cd6",
      "#f2b63d",
      "#d46e33",
      "#e34262",
      "#94353d",
      "#57253b",
      "#9c656c",
      "#d1b48c",
      "#b4ba47",
      "#6d8c32",
      "#2c1b2e",
    ];

    if (!savedPalette || savedPalette === "[]") {
      return defaultPalette;
    }

    try {
      const parsedPalette: string[] = JSON.parse(savedPalette);
      if (Array.isArray(parsedPalette)) {
        return parsedPalette;
      }
    } catch (error) {
      console.error("Failed to parse palette from localStorage:", error);
    }
    return defaultPalette;
  });

  const handleDrop = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      const file = e.dataTransfer.files[0];
      if (file.type.startsWith("image/")) {
        setImage(file);
        e.dataTransfer.clearData();
      } else {
        setDragBoxText("Please drop a valid image file.");
      }
    }
  };

  const tryProcessImage = () => {
    if (image && paletteColors.length > 0) {
      processImage(
        image,
        document.getElementById("pixelCanvas") as HTMLCanvasElement,
        paletteColors,
        resolution
      );
    }
  };

  useEffect(() => {
    localStorage.setItem("resolution", resolution.toString());
  }, [resolution]);

  useEffect(() => {
    tryProcessImage();
  }, [image, paletteColors, resolution]);

  const [titleColors, setTitleColors] = useState<string[]>(() =>
    "Pixellizer".split("").map(() => `hsl(${Math.random() * 360}, 100%, 50%)`)
  );

  return (
    <div className="min-h-screen bg-[#161616] relative overflow-hidden font-pixel">
      <div className="fixed inset-0 z-0">
        <div className="absolute inset-0 bg-[#161616] opacity-90"></div>
        <div className="absolute inset-0 bg-[linear-gradient(45deg,#161616_25%,transparent_25%),linear-gradient(-45deg,#161616_25%,transparent_25%),linear-gradient(45deg,transparent_75%,#161616_75%),linear-gradient(-45deg,transparent_75%,#161616_75%)] bg-[length:20px_20px] animate-pixelMove"></div>
      </div>

      <div className="relative z-10 text-white text-xl flex flex-col mt-5 mx-64">
        <h1 className="mx-auto text-[6rem] font-pixel my-20">
          {"Pixellizer".split("").map((char, index) => (
            <span
              key={index}
              style={{
                color: titleColors[index],
              }}
            >
              {char}
            </span>
          ))}
        </h1>

        <div className="flex flex-row gap-5 justify-center">
          <div
            className="h-[256px] w-[256px]  bg-slate-400 relative pixel-border text-center"
            onDrop={handleDrop}
            onDragOver={(e) => {
              e.preventDefault();
              setDragBoxText("Drop here!");
            }}
            onDragLeave={(e) => {
              e.preventDefault();
              setDragBoxText("Drop an image to start.");
            }}
          >
            <div
              hidden={image != null}
              className="absolute inset-1 border-2 border-dashed border-white rounded-lg pointer-events-none"
            ></div>
            {image ? (
              <img
                src={URL.createObjectURL(image)}
                alt="Input Image"
                className="h-full w-full object-cover rounded-lg"
              />
            ) : (
              <p className="text-black text-2xl flex items-center justify-center h-full">
                {dragBoxText}
              </p>
            )}
          </div>
          <div className="h-[256px] w-[256px] bg-slate-400 pixel-border">
            <canvas id="pixelCanvas" className="h-full w-full"></canvas>
          </div>
        </div>

        <div className="flex flex-col mt-8">
          <h3 className="text-2xl">Palette</h3>
          <div className="border-b-[1px] border-slate-300 mb-2"></div>

          <PaletteCreator
            paletteColors={paletteColors}
            setPaletteColors={setPaletteColors}
          />

          <div className="mt-8">
            <h3 className="text-2xl">Dimensions</h3>
            <div className="border-b-[1px] border-slate-300 mb-2"></div>
            <Dropdown
              selectedValue={resolution}
              setSelectedValues={setResolution}
            ></Dropdown>
          </div>
        </div>


        <div className="flex flex-col mx-auto w-[40rem]">
          <h3 className="mt-20 mb-4 text-5xl text-center">About</h3>
          <div className="border-b-[1px] border-slate-300 mb-2"></div>
          <p className="">
            Pixellizer is a free tool to convert existing art into pixel art
            while conforming to a specified palette. If you found it useful you
            could consider supporting me.
            <br />
            <br />
            No pressure though, this tool is free to use and open source!
            <span>
              <a href="https://buymeacoffee.com/uzeeyo">
                <img
                  className="mt-10 mb-20 rounded-3xl mx-auto w-[15rem]"
                  src="data:image/png;base64,"
                />
              </a>
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
